target_book = input()books_count = -1command = input()while command != "No More Books":    books_count += 1    if target_book == command:        break    command = input()if command == "No More Books":    books_count += +1if command == "No More Books":    print("The book you search is not here!")    print(f"You checked {books_count} books.")else:    print(f"You checked {books_count} books and found it.")